import React, { useState, useEffect } from 'react';
import { getAuth, signInWithPopup, GoogleAuthProvider, onAuthStateChanged } from 'firebase/auth';
import { app } from './firebase'; // تأكد أن firebase.js موجود ومُعد فيه Firebase
import { collection, addDoc, serverTimestamp, getDocs, orderBy, query } from 'firebase/firestore';
import { db } from './firebase'; // أو firebaseConfig حسب اسم الملف
import './App.css';
import { signOut } from "firebase/auth";


function App() {
  const [user, setUser] = useState(null);
  const [nickname, setNickname] = useState('');
  const [message, setMessage] = useState('');
  const [sent, setSent] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [messages, setMessages] = useState([]);
  const [showPassword, setShowPassword] = useState(false); // حالة إظهار كلمة المرور
  const [password, setPassword] = useState('');

  const auth = getAuth(app);
  const provider = new GoogleAuthProvider();
  const adminEmail = "adham.gh1111@gmail.com"; // ← عدّل الإيميل حسب ما تريد

  const handleLogin = async () => {
    try {
      await signInWithPopup(auth, provider);
    } catch (err) {
      alert("حدث خطأ أثناء تسجيل الدخول");
      console.error(err);
    }
  };

  const handleLogout = () => {
    signOut(auth)
      .then(() => console.log("تم تسجيل الخروج"))
      .catch((error) => console.error("خطأ:", error));
  };


  const handleSendMessage = async () => {
    if (!user) return alert("يجب تسجيل الدخول أولاً!");
    if (!nickname.trim() || !message.trim()) return alert("يرجى ملء جميع الحقول!");

    try {
      await addDoc(collection(db, 'messages'), {
        nickname,
        message,
        uid: user.uid,
        createdAt: serverTimestamp()
      });

      setSent(true);
      setMessage('');
    } catch (error) {
      alert("حدث خطأ أثناء إرسال الرسالة");
      console.error(error);
    }
  };

  const togglePassword = () => {
    setShowPassword(!showPassword);
  };


  useEffect(() => {
    onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
      setIsAdmin(currentUser?.email === adminEmail);
    });
  }, [auth]);

  useEffect(() => {
    const fetchMessages = async () => {
      if (isAdmin) {
        const q = query(collection(db, 'messages'), orderBy('createdAt', 'desc'));
        const querySnapshot = await getDocs(q);
        const fetchedMessages = querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
        setMessages(fetchedMessages);
      }
    };

    fetchMessages();
  }, [isAdmin, auth]);





  return ( 
    <div className="min-h-screen flex items-center justify-center bg-gray-100 p-4">
      <div className="bg-white rounded-2xl shadow-xl p-6 w-full max-w-md text-center">

        {/* زر تسجيل الخروج إذا كان المشرف */}
        {user && isAdmin && (
          <div className="text-right mb-4">
            <button
              onClick={handleLogout}
              className="bg-red-500 text-white px-6 py-2 rounded-lg hover:bg-red-600 transition"
            >
              تسجيل الخروج
            </button>
          </div>
        )}

        {/* إذا لم يسجل المستخدم الدخول */}
        {!user ? (
          <div className="space-y-8">
            {/* تسجيل الدخول التقليدي */}
            <div className="border p-4 rounded-xl shadow-sm">
              <h2 className="text-lg font-bold mb-4">تسجيل الدخول</h2>

              <input
                type="text"
                placeholder="البريد الإلكتروني"
                className="border p-2 w-full rounded mb-3"
              />

              {/* حقل كلمة المرور مع التحكم بالإظهار والإخفاء */}
              <input
                  type={showPassword ? "text" : "password"}
                  placeholder="كلمة المرور"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className="input-field"
              />

  <div className="checkbox-container">
  <label htmlFor="showPassword" className="checkbox-label">عرض كلمة المرور</label>
  <input
    type="checkbox"
    id="showPassword"
    checked={showPassword}
    onChange={togglePassword}
  />
</div>

              <button
                onClick={() => alert("تسجيل الدخول التقليدي قيد التطوير 😅")}
                className="bg-blue-500 text-white px-4 py-2 rounded-xl hover:bg-blue-600 w-full"
              >
                تسجيل الدخول
              </button>
            </div>

            {/* تسجيل الدخول بحساب Google */}
            <div className="border p-4 rounded-xl shadow-sm">
              <h2 className="text-lg font-bold mb-4">تسجيل دخول مباشر</h2>
              <button
                onClick={handleLogin}
                className="bg-red-500 text-white px-4 py-2 rounded-xl hover:bg-red-600 w-full flex items-center justify-center space-x-2"
              >
                <i className="fab fa-google"></i>
                <span>تسجيل الدخول بحساب جوجل</span>
              </button>
            </div>
          </div>
        ) : isAdmin ? (
          // عرض الرسائل للمشرف
          <div>
            <h2 className="text-lg font-semibold mb-2">📨 جميع الرسائل</h2>
            <div className="text-right space-y-2 max-h-96 overflow-y-auto">
              {messages.map((msg) => (
                <div key={msg.id} className="bg-gray-100 p-3 rounded text-sm text-gray-700">
                  <strong>{msg.nickname}:</strong> {msg.message}
                </div>
              ))}
            </div>
          </div>
        ) : (
          // إرسال رسالة للمستخدم العادي
          <>
          <h2 className="text-xl font-semibold mb-4">✉️ إرسال رسالة مجهولة</h2>

          <input
            type="text"
            placeholder="اسمك المستعار"
            value={nickname}
            onChange={(e) => setNickname(e.target.value)}
            className="border p-2 w-full rounded mb-3"
          />

          <textarea
            placeholder="اكتب رسالتك هنا بدون ان يعرفك..."
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            className="border p-2 w-full rounded mb-3 h-28 resize-none"
          />

          <button
            onClick={handleSendMessage}
            disabled={!user}
            className={`w-full px-4 py-2 rounded-xl text-white ${user ? 'bg-blue-600 hover:bg-blue-700' : 'bg-gray-400 cursor-not-allowed'}`}
          >
            إرسال الرسالة
          </button>

          {sent && <p className="text-green-500 mt-3">تم إرسال رسالتك بنجاح ✅</p>}

          {/* إضافة مسافة بين الفورم وزر الخروج */}
          <div className="mt-6"> {/* إضافة مساحة قبل زر تسجيل الخروج */}
            <div className="text-center">
              <button
                onClick={handleLogout}
                className="bg-red-500 text-white px-6 py-2 rounded-lg hover:bg-red-600 transition w-full"
              >
                تسجيل الخروج
              </button>
            </div>
          </div>
        </>


        )}
      </div>
    </div>
  );

}

export default App;
