import React, { useState } from 'react';
import { getAuth, sendPasswordResetEmail } from 'firebase/auth';
import { app } from '../firebase'; // استيراد التطبيق
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './ForgotPassword.css'; // ملف CSS اختياري لو حابب

const auth = getAuth(app);

function ForgotPassword() {
  const [email, setEmail] = useState('');

  const handlePasswordReset = async (e) => {
    e.preventDefault();
    if (!email) {
      toast.error('يرجى إدخال البريد الإلكتروني');
      return;
    }

    try {
      await sendPasswordResetEmail(auth, email);
      toast.success('تم إرسال رابط استعادة كلمة المرور إلى بريدك الإلكتروني');
    } catch (error) {
      console.error(error);
      toast.error('حدث خطأ أثناء إرسال رابط استعادة كلمة المرور: ' + error.message);
    }
  };

  return (
    <div className="forgot-password-container">
      <h2>نسيت كلمة المرور</h2>
      <form onSubmit={handlePasswordReset}>
        <input
          type="email"
          placeholder="أدخل بريدك الإلكتروني"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <button type="submit">إرسال رابط الاستعادة</button>
      </form>

      <ToastContainer
        position="top-center"
        autoClose={3000}
        hideProgressBar={false}
        newestOnTop
        closeOnClick
        rtl
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="colored"
      />
    </div>
  );
}

export default ForgotPassword;
