import React, { useState, useEffect } from 'react';
import { getAuth, signInWithPopup, GoogleAuthProvider, onAuthStateChanged } from 'firebase/auth';
import { app } from './firebase'; // ÿ™ÿ£ŸÉÿØ ÿ£ŸÜ firebase.js ŸÖŸàÿ¨ŸàÿØ ŸàŸÖŸèÿπÿØ ŸÅŸäŸá Firebase
import { collection, addDoc, serverTimestamp, getDocs, orderBy, query } from 'firebase/firestore';
import { db } from './firebase'; // ÿ£Ÿà firebaseConfig ÿ≠ÿ≥ÿ® ÿßÿ≥ŸÖ ÿßŸÑŸÖŸÑŸÅ
import './App.css';
import { signOut } from "firebase/auth";


function App() {
  const [user, setUser] = useState(null);
  const [nickname, setNickname] = useState('');
  const [message, setMessage] = useState('');
  const [sent, setSent] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [messages, setMessages] = useState([]);

  const auth = getAuth(app);
  const provider = new GoogleAuthProvider();
  const adminEmail = "adham.gh1111@gmail.com"; // ‚Üê ÿπÿØŸëŸÑ ÿßŸÑÿ•ŸäŸÖŸäŸÑ ÿ≠ÿ≥ÿ® ŸÖÿß ÿ™ÿ±ŸäÿØ

  const handleLogin = async () => {
    try {
      await signInWithPopup(auth, provider);
    } catch (err) {
      alert("ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ");
      console.error(err);
    }
  };

  const handleLogout = () => {
    signOut(auth)
      .then(() => console.log("ÿ™ŸÖ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨"))
      .catch((error) => console.error("ÿÆÿ∑ÿ£:", error));
  };


  const handleSendMessage = async () => {
    if (!user) return alert("Ÿäÿ¨ÿ® ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ÿ£ŸàŸÑÿßŸã!");
    if (!nickname.trim() || !message.trim()) return alert("Ÿäÿ±ÿ¨Ÿâ ŸÖŸÑÿ° ÿ¨ŸÖŸäÿπ ÿßŸÑÿ≠ŸÇŸàŸÑ!");

    try {
      await addDoc(collection(db, 'messages'), {
        nickname,
        message,
        uid: user.uid,
        createdAt: serverTimestamp()
      });

      setSent(true);
      setMessage('');
    } catch (error) {
      alert("ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑÿ±ÿ≥ÿßŸÑÿ©");
      console.error(error);
    }
  };

  const togglePassword = () => {
    // ÿØÿßŸÑÿ© ŸÑÿ™ÿ®ÿØŸäŸÑ ÿ≠ÿßŸÑÿ© ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±
    setShowPassword(!showPassword);
  };


  useEffect(() => {
    onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
      setIsAdmin(currentUser?.email === adminEmail);
    });
  }, [auth]);

  useEffect(() => {
    const fetchMessages = async () => {
      if (isAdmin) {
        const q = query(collection(db, 'messages'), orderBy('createdAt', 'desc'));
        const querySnapshot = await getDocs(q);
        const fetchedMessages = querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
        setMessages(fetchedMessages);
      }
    };

    fetchMessages();
  }, [isAdmin, auth]);





  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100 p-4">
      <div className="bg-white rounded-2xl shadow-xl p-6 w-full max-w-md text-center">

        {/* ÿßŸÑŸáŸäÿØÿ± ÿßŸÑÿ∞Ÿä Ÿäÿ≠ÿ™ŸàŸä ÿπŸÑŸâ ÿ≤ÿ± ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨ ŸÑŸÑŸÖÿ¥ÿ±ŸÅ ŸÅŸÇÿ∑ */}
        {user && isAdmin && (
          <div className="text-right mb-4">
            <button
              onClick={handleLogout}
              className="bg-red-500 text-white px-6 py-2 rounded-lg hover:bg-red-600 transition"
            >
              ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨
            </button>
          </div>
        )}

        {/* ÿ•ÿ∞ÿß ŸÉÿßŸÜ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸÑŸäÿ≥ ŸÖÿ≥ÿ¨ŸÑ ÿØÿÆŸàŸÑŸá ÿ®ÿπÿØ */}
        {!user ? (
          <div className="space-y-8">
            {/* ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ÿßŸÑÿ™ŸÇŸÑŸäÿØŸä */}
            <div className="border p-4 rounded-xl shadow-sm">
              <h2 className="text-lg font-bold mb-4">ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ </h2>
              <input
                type="text"
                placeholder="ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä"
                className="border p-2 w-full rounded mb-3"
              />
              <input
                type="password"
                placeholder="ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±"
                className="border p-2 w-full rounded mb-3"
              />
              <button
                onClick={() => alert("ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ÿßŸÑÿ™ŸÇŸÑŸäÿØŸä ŸÇŸäÿØ ÿßŸÑÿ™ÿ∑ŸàŸäÿ± üòÖ")}
                className="bg-blue-500 text-white px-4 py-2 rounded-xl hover:bg-blue-600 w-full"
              >
                ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ
              </button>
            </div>

            {/* ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ÿπÿ®ÿ± Google */}
            <div className="border p-4 rounded-xl shadow-sm">
              <h2 className="text-lg font-bold mb-4">ÿ™ÿ≥ÿ¨ŸäŸÑ ÿØÿÆŸàŸÑ ŸÖÿ®ÿßÿ¥ÿ± </h2>
              <button
  onClick={handleLogin}
  className="bg-red-500 text-white px-4 py-2 rounded-xl hover:bg-red-600 w-full flex items-center justify-center space-x-2"
>
  <i className="fab fa-google"></i> {/* ÿ£ŸäŸÇŸàŸÜÿ© ÿ¨Ÿàÿ¨ŸÑ */}
  <span>ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ÿ®ÿ≠ÿ≥ÿßÿ® ÿ¨Ÿàÿ¨ŸÑ</span>
</button>

            </div>
          </div>
        ) : isAdmin ? (
          // ÿ•ÿ∞ÿß ŸÉÿßŸÜ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸÖÿ¥ÿ±ŸÅŸãÿß
          <div>
            <h2 className="text-lg font-semibold mb-2">üì® ÿ¨ŸÖŸäÿπ ÿßŸÑÿ±ÿ≥ÿßÿ¶ŸÑ</h2>
            <div className="text-right space-y-2 max-h-96 overflow-y-auto">
              {messages.map((msg) => (
                <div key={msg.id} className="bg-gray-100 p-3 rounded text-sm text-gray-700">
                  <strong>{msg.nickname}:</strong> {msg.message}
                </div>
              ))}
            </div>
          </div>
        ) : (
          // ÿ•ÿ∞ÿß ŸÉÿßŸÜ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ÿπÿßÿØŸäŸãÿß
          <>
            <h2 className="text-xl font-semibold mb-4">‚úâÔ∏è ÿ•ÿ±ÿ≥ÿßŸÑ ÿ±ÿ≥ÿßŸÑÿ© ŸÖÿ¨ŸáŸàŸÑÿ©</h2>

            <input
              type="text"
              placeholder="ÿßÿ≥ŸÖŸÉ ÿßŸÑŸÖÿ≥ÿ™ÿπÿßÿ±"
              value={nickname}
              onChange={(e) => setNickname(e.target.value)}
              className="border p-2 w-full rounded mb-3"
            />

            <textarea
              placeholder="ÿßŸÉÿ™ÿ® ÿ±ÿ≥ÿßŸÑÿ™ŸÉ ŸáŸÜÿß..."
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              className="border p-2 w-full rounded mb-3 h-28 resize-none"
            />

            <button
