import React, { useState, useEffect } from 'react';
import { getAuth, signInWithPopup, GoogleAuthProvider, onAuthStateChanged } from 'firebase/auth';
import { app } from './firebase'; // تأكد أن firebase.js موجود ومُعد فيه Firebase
import { collection, addDoc, serverTimestamp } from 'firebase/firestore';
import './App.css';

function App() {
  const [user, setUser] = useState(null);
  const [nickname, setNickname] = useState('');
  const [message, setMessage] = useState('');
  const [sent, setSent] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [messages, setMessages] = useState([]);

  const auth = getAuth(app);
  const provider = new GoogleAuthProvider();

  const handleLogin = async () => {
    try {
      await signInWithPopup(auth, provider);
    } catch (err) {
      alert("حدث خطأ أثناء تسجيل الدخول");
      console.error(err);
    }
  };

  const handleSendMessage = async () => {
    if (!user) return alert("يجب تسجيل الدخول أولاً!");
    if (!nickname.trim() || !message.trim()) return alert("يرجى ملء جميع الحقول!");

    try {
      await addDoc(collection(db, 'messages'), {
        nickname,
        message,
        uid: user.uid,
        createdAt: serverTimestamp()
      });

      setSent(true);
      setMessage('');
    } catch (error) {
      alert("حدث خطأ أثناء إرسال الرسالة");
      console.error(error);
    }
  };


  useEffect(() => {
    onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
      setIsAdmin(currentUser?.email === adminEmail);
    });
  }, []);


  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100 p-4">
      <div className="bg-white rounded-2xl shadow-xl p-6 w-full max-w-md text-center">
        <h1 className="text-2xl font-bold mb-4">أرسل رسالة مجهولة</h1>

        {!user ? (
          <button
            onClick={handleLogin}
            className="bg-red-500 text-white px-4 py-2 rounded-xl hover:bg-red-600 mb-4 w-full"
          >
            تسجيل الدخول بحساب Google
          </button>
        ) : (
          <p className="text-green-600 mb-2">تم تسجيل الدخول كـ {user.email}</p>
        )}

        <input
          type="text"
          placeholder="اسمك المستعار"
          value={nickname}
          onChange={(e) => setNickname(e.target.value)}
          className="border p-2 w-full rounded mb-3"
        />

        <textarea
          placeholder="اكتب رسالتك هنا..."
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          className="border p-2 w-full rounded mb-3 h-28 resize-none"
        />

        <button
          onClick={handleSendMessage}
          disabled={!user}
          className={`w-full px-4 py-2 rounded-xl text-white ${user ? 'bg-blue-600 hover:bg-blue-700' : 'bg-gray-400 cursor-not-allowed'}`}
        >
          إرسال الرسالة
        </button>

        {sent && <p className="text-green-500 mt-3">تم إرسال رسالتك بنجاح ✅</p>}
      </div>
    </div>
  );
}

export default App;
