import React, { useState, useEffect } from 'react';
import { getAuth, signInWithPopup, GoogleAuthProvider, onAuthStateChanged } from 'firebase/auth';
import { app } from './firebase'; // تأكد أن firebase.js موجود ومُعد فيه Firebase
import { collection, addDoc, serverTimestamp, getDocs, orderBy, query } from 'firebase/firestore';
import { db } from './firebase'; // أو firebaseConfig حسب اسم الملف
import './App.css';
import { signOut } from "firebase/auth";


function App() {
  const [user, setUser] = useState(null);
  const [nickname, setNickname] = useState('');
  const [message, setMessage] = useState('');
  const [sent, setSent] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [messages, setMessages] = useState([]);

  const auth = getAuth(app);
  const provider = new GoogleAuthProvider();
  const adminEmail = "adham.gh1111@gmail.com"; // ← عدّل الإيميل حسب ما تريد

  const handleLogin = async () => {
    try {
      await signInWithPopup(auth, provider);
    } catch (err) {
      alert("حدث خطأ أثناء تسجيل الدخول");
      console.error(err);
    }
  };

  const handleLogout = () => {
    signOut(auth)
      .then(() => console.log("تم تسجيل الخروج"))
      .catch((error) => console.error("خطأ:", error));
  };


  const handleSendMessage = async () => {
    if (!user) return alert("يجب تسجيل الدخول أولاً!");
    if (!nickname.trim() || !message.trim()) return alert("يرجى ملء جميع الحقول!");

    try {
      await addDoc(collection(db, 'messages'), {
        nickname,
        message,
        uid: user.uid,
        createdAt: serverTimestamp()
      });

      setSent(true);
      setMessage('');
    } catch (error) {
      alert("حدث خطأ أثناء إرسال الرسالة");
      console.error(error);
    }
  };


  useEffect(() => {
    onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
      setIsAdmin(currentUser?.email === adminEmail);
    });
  }, []);

  useEffect(() => {
    const fetchMessages = async () => {
      if (isAdmin) {
        const q = query(collection(db, 'messages'), orderBy('createdAt', 'desc'));
        const querySnapshot = await getDocs(q);
        const fetchedMessages = querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
        setMessages(fetchedMessages);
      }
    };

    fetchMessages();
  }, [isAdmin]);

    // متابعة حالة المستخدم
    useEffect(() => {
      const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
        setUser(currentUser);
      });
      return () => unsubscribe();
    }, []);

    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100 p-4">
        <div className="bg-white rounded-2xl shadow-xl p-6 w-full max-w-md text-center">

          {/* الهيدر الذي يحتوي على زر تسجيل الخروج للمشرف فقط */}
          {user && isAdmin && (
            <div className="text-right mb-4">
              <button
                onClick={handleLogout}
                className="bg-red-500 text-white px-6 py-2 rounded-lg hover:bg-red-600 transition"
              >
                تسجيل الخروج
              </button>
            </div>
          )}

          {/* إذا كان المستخدم ليس مسجل دخوله بعد */}
          {!user ? (
            <button
              onClick={handleLogin}
              className="bg-red-500 text-white px-4 py-2 rounded-xl hover:bg-red-600 mb-4 w-full"
            >
              تسجيل الدخول بحساب Google
            </button>
          ) : isAdmin ? (
            // إذا كان المستخدم مشرفًا
            <div>
              <h2 className="text-lg font-semibold mb-2">📨 جميع الرسائل</h2>
              <div className="text-right space-y-2 max-h-96 overflow-y-auto">
                {messages.map((msg) => (
                  <div key={msg.id} className="bg-gray-100 p-3 rounded text-sm text-gray-700">
                    <strong>{msg.nickname}:</strong> {msg.message}
                  </div>
                ))}
              </div>
            </div>
          ) : (
            // إذا كان المستخدم عاديًا
            <>
              <input
                type="text"
                placeholder="اسمك المستعار"
                value={nickname}
                onChange={(e) => setNickname(e.target.value)}
                className="border p-2 w-full rounded mb-3"
              />

              <textarea
                placeholder="اكتب رسالتك هنا..."
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                className="border p-2 w-full rounded mb-3 h-28 resize-none"
              />

              <button
                onClick={handleSendMessage}
                disabled={!user}
                className={`w-full px-4 py-2 rounded-xl text-white ${user ? 'bg-blue-600 hover:bg-blue-700' : 'bg-gray-400 cursor-not-allowed'}`}
              >
                إرسال الرسالة
              </button>

              {sent && <p className="text-green-500 mt-3">تم إرسال رسالتك بنجاح ✅</p>}

              {/* زر تسجيل الخروج للمستخدم العادي */}
              <div className="text-center mt-4">
                <button
                  onClick={handleLogout}
                  className="bg-red-500 text-white px-6 py-2 rounded-lg hover:bg-red-600 transition"
                >
                  تسجيل الخروج
                </button>
              </div>
            </>
          )}
        </div>
      </div>
    );

}

export default App;
