import React, { useState, useEffect } from 'react';
import { getAuth, signInWithPopup, GoogleAuthProvider, onAuthStateChanged, signOut } from 'firebase/auth';
import { app } from './firebase'; // ØªØ£ÙƒØ¯ Ø£Ù† firebase.js Ù…ÙˆØ¬ÙˆØ¯ ÙˆÙ…ÙØ¹Ø¯ ÙÙŠÙ‡ Firebase
import { collection, addDoc, serverTimestamp, getDocs, orderBy, query, deleteDoc, doc } from 'firebase/firestore';
import { db } from './firebase'; // Ø£Ùˆ firebaseConfig Ø­Ø³Ø¨ Ø§Ø³Ù… Ø§Ù„Ù…Ù„Ù
import './App.css';
import { Routes, Route, BrowserRouter as Router } from 'react-router-dom';  // ØªØ£ÙƒØ¯ Ù…Ù† Ø¥Ø¶Ø§ÙØ© Routes
import PrivacyPolicy from './pages/PrivacyPolicy';
import TermsOfUse from './pages/TermsOfUse';

function Header() {
  return (
    <header className="bg-blue-600 text-white p-4 flex justify-between items-center">
      <nav>
        <ul className="flex space-x-6">
          <li>
            <Link to="/privacy-policy" className="hover:text-gray-300">Ø³ÙŠØ§Ø³Ø© Ø§Ù„Ø®ØµÙˆØµÙŠØ©</Link>
          </li>
          <li>
            <Link to="/terms-of-use" className="hover:text-gray-300">Ø´Ø±ÙˆØ· Ø§Ù„Ø¥Ø³ØªØ®Ø¯Ø§Ù…</Link>
          </li>
          <li>
            <Link to="/" className="hover:text-gray-300">Ø§Ù„Ø±Ø¦ÙŠØ³ÙŠØ©</Link>
          </li>
          {!localStorage.getItem('user') && (
            <>
              <li>
                <Link to="/register" className="hover:text-gray-300">ØªØ³Ø¬ÙŠÙ„</Link>
              </li>
              <li>
                <Link to="/login" className="hover:text-gray-300">Ø¯Ø®ÙˆÙ„</Link>
              </li>
            </>
          )}
        </ul>
      </nav>
      <div className="text-xl font-bold">
        <Link to="/" className="text-white">
          <img src="/secret.png" alt="Ø´Ø¹Ø§Ø± Ø§Ù„Ù…ÙˆÙ‚Ø¹" className="h-8" />
        </Link>
      </div>
    </header>
  );
}

function HomePage() {
  const [user, setUser] = useState(null);
  const [nickname, setNickname] = useState('');
  const [message, setMessage] = useState('');
  const [sent, setSent] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [messages, setMessages] = useState([]);
  const [switchOn, setSwitchOn] = useState(false); // Ø­Ø§Ù„Ø© Ø§Ù„Ø³ÙˆÙŠØªØ´
  const [charCount, setCharCount] = useState(0);
  const [loading, setLoading] = useState(true);
  const [isModalOpen, setIsModalOpen] = useState(false); // Ù„ØªØ­Ø¯ÙŠØ¯ Ø¥Ø°Ø§ ÙƒØ§Ù†Øª Ø§Ù„Ù†Ø§ÙØ°Ø© Ø§Ù„Ù…Ù†Ø¨Ø«Ù‚Ø© Ù…ÙØªÙˆØ­Ø© Ø£Ù… Ù„Ø§
  const [selectedMessage, setSelectedMessage] = useState(null); // Ù„Ø­ÙØ¸ Ø§Ù„Ø±Ø³Ø§Ù„Ø© Ø§Ù„Ù…Ø®ØªØ§Ø±Ø©
  const maxCharCount = 500;

  const auth = getAuth(app);
  const provider = new GoogleAuthProvider();
  const adminEmail = "adham.gh1111@gmail.com";

  const handleLogin = async () => {
    try {
      const result = await signInWithPopup(auth, provider);
      localStorage.setItem('user', JSON.stringify(result.user));
      setUser(result.user);
    } catch (err) {
      alert("Ø­Ø¯Ø« Ø®Ø·Ø£ Ø£Ø«Ù†Ø§Ø¡ ØªØ³Ø¬ÙŠÙ„ Ø§Ù„Ø¯Ø®ÙˆÙ„");
      console.error(err);
    }
  };

  const handleLogout = () => {
    const confirmLogout = window.confirm("Ù‡Ù„ Ø£Ù†Øª Ù…ØªØ£ÙƒØ¯ Ù…Ù† ØªØ³Ø¬ÙŠÙ„ Ø§Ù„Ø®Ø±ÙˆØ¬ØŸ");

    if (confirmLogout) {
      signOut(auth)
        .then(() => {
          console.log("ØªÙ… ØªØ³Ø¬ÙŠÙ„ Ø§Ù„Ø®Ø±ÙˆØ¬");
          localStorage.removeItem('user');
          setUser(null);
          setIsAdmin(false);
        })
        .catch((error) => console.error("Ø®Ø·Ø£:", error));
    } else {
      console.log("ØªÙ… Ø¥Ù„ØºØ§Ø¡ Ø¹Ù…Ù„ÙŠØ© ØªØ³Ø¬ÙŠÙ„ Ø§Ù„Ø®Ø±ÙˆØ¬");
    }
  };


  const handleSendMessage = async (e) => {
    e.preventDefault(); // ğŸ‘ˆ Ù‡Ø°Ø§ ÙŠÙ…Ù†Ø¹ Ø¥Ø¹Ø§Ø¯Ø© ØªØ­Ù…ÙŠÙ„ Ø§Ù„ØµÙØ­Ø©

    if (!user) return alert("ÙŠØ¬Ø¨ ØªØ³Ø¬ÙŠÙ„ Ø§Ù„Ø¯Ø®ÙˆÙ„ Ø£ÙˆÙ„Ø§Ù‹!");
    if (!nickname.trim() || !message.trim()) return alert("ÙŠØ±Ø¬Ù‰ Ù…Ù„Ø¡ Ø¬Ù…ÙŠØ¹ Ø§Ù„Ø­Ù‚ÙˆÙ„!");

    try {
      // Ø¥Ø±Ø³Ø§Ù„ Ø§Ù„Ø±Ø³Ø§Ù„Ø© Ø¥Ù„Ù‰ Firebase
      const docRef = await addDoc(collection(db, 'messages'), {
        nickname,
        message,
        uid: user.uid,
        createdAt: serverTimestamp()
      });

      console.log("ØªÙ… Ø¥Ø±Ø³Ø§Ù„ Ø§Ù„Ø±Ø³Ø§Ù„Ø© Ø¨Ù†Ø¬Ø§Ø­: ", docRef.id); // Ø¥Ø¶Ø§ÙØ© log Ù‡Ù†Ø§
      setSent(true);
      setMessage('');
      setNickname('');
    } catch (error) {
      alert("Ø­Ø¯Ø« Ø®Ø·Ø£ Ø£Ø«Ù†Ø§Ø¡ Ø¥Ø±Ø³Ø§Ù„ Ø§Ù„Ø±Ø³Ø§Ù„Ø©");
      console.error(error);
    }
  };


  const toggleSwitch = () => {
    setSwitchOn(!switchOn);

    if (!switchOn) {
      alert("Ø¬Ù…ÙŠØ¹ Ù…Ø¹Ù„ÙˆÙ…Ø§ØªÙƒ Ø³Ø±ÙŠØ© Ø§Ù„Ø§Ù†");
    } else {
      alert("Ø³ÙŠØªÙ… Ø§Ù„Ø§Ù† Ø¥Ø¸Ù‡Ø§Ø± Ù…Ø¹Ù„ÙˆÙ…Ø§ØªÙƒ Ø§Ù„Ø´Ø®ØµÙŠØ© ");
    }
  };

  const handleMessageChange = (e) => {
    const newMessage = e.target.value;
    if (newMessage.length <= maxCharCount) {
      setMessage(newMessage);
      setCharCount(newMessage.length); // ØªØ­Ø¯ÙŠØ« Ø¹Ø¯Ø¯ Ø§Ù„Ø­Ø±ÙˆÙ
    }
  };
  const handleDeleteMessage = async (id) => {
    const confirmDelete = window.confirm("Ù‡Ù„ ØªØ±ÙŠØ¯ Ø­Ø°Ù Ù‡Ø°Ù‡ Ø§Ù„Ø±Ø³Ø§Ù„Ø©ØŸ");

    if (confirmDelete) {
      try {
        await deleteDoc(doc(db, "messages", id));
        setMessages(messages.filter((msg) => msg.id !== id)); // Ù†Ø­Ø°ÙÙ‡Ø§ ÙƒÙ…Ø§Ù† Ù…Ù† Ø§Ù„ÙˆØ§Ø¬Ù‡Ø©
        alert("ØªÙ… Ø­Ø°Ù Ø§Ù„Ø±Ø³Ø§Ù„Ø© Ø¨Ù†Ø¬Ø§Ø­");
      } catch (error) {
        console.error("Ø®Ø·Ø£ Ø£Ø«Ù†Ø§Ø¡ Ø§Ù„Ø­Ø°Ù:", error);
        alert("Ø­Ø¯Ø« Ø®Ø·Ø£ Ø£Ø«Ù†Ø§Ø¡ Ø­Ø°Ù Ø§Ù„Ø±Ø³Ø§Ù„Ø©");
      }
    }
  };
  const handleShowDetails = (message) => {
    setSelectedMessage(message);
    setIsModalOpen(true); // ÙØªØ­ Ø§Ù„Ù†Ø§ÙØ°Ø© Ø§Ù„Ù…Ù†Ø¨Ø«Ù‚Ø©
  };

  const handleCloseModal = () => {
    setIsModalOpen(false); // Ø¥ØºÙ„Ø§Ù‚ Ø§Ù„Ù†Ø§ÙØ°Ø© Ø§Ù„Ù…Ù†Ø¨Ø«Ù‚Ø©
  };


  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }

    onAuthStateChanged(auth, (currentUser) => {
      if (currentUser) {
        localStorage.setItem('user', JSON.stringify(currentUser));
        setUser(currentUser);
        setIsAdmin(currentUser?.email === adminEmail);
      } else {
        localStorage.removeItem('user');
        setUser(null);
        setIsAdmin(false);
      }
      setLoading(false);   // Ø¹Ù†Ø¯ Ø§ÙƒØªÙ…Ø§Ù„ Ø§Ù„ØªØ­Ù‚Ù‚ Ù…Ù† Ø­Ø§Ù„Ø© Ø§Ù„Ù…Ø³ØªØ®Ø¯Ù…
    });
  }, [auth, adminEmail]);

  useEffect(() => {
    const fetchMessages = async () => {
      if (isAdmin) {
        const q = query(collection(db, 'messages'), orderBy('createdAt', 'desc'));
        const querySnapshot = await getDocs(q);
        const fetchedMessages = querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
        console.log(fetchedMessages); // Ø¥Ø¶Ø§ÙØ© log Ù‡Ù†Ø§ Ù„Ù„ØªØ­Ù‚Ù‚
        setMessages(fetchedMessages);
      }
    };

    fetchMessages();
  }, [isAdmin, db]);

  if (loading) {
    return <div> </div>;
  }


  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100 p-4">
      <div className="bg-white rounded-2xl shadow-xl p-6 w-full max-w-md text-center">

        {/* Ø²Ø± ØªØ³Ø¬ÙŠÙ„ Ø§Ù„Ø®Ø±ÙˆØ¬ Ø¥Ø°Ø§ ÙƒØ§Ù† Ø§Ù„Ù…Ø´Ø±Ù */}
