import React, { useState, useEffect } from 'react';
import { getAuth, signInWithPopup, GoogleAuthProvider, onAuthStateChanged,signInWithEmailAndPassword } from 'firebase/auth';
import { app } from './firebase'; 
import { collection, addDoc, serverTimestamp, getDocs, orderBy, query } from 'firebase/firestore';
import { db,analytics} from './firebase'; 
import './App.css';
import { signOut } from "firebase/auth";
import { deleteDoc, doc } from "firebase/firestore"; 
import { Link, Routes, Route } from 'react-router-dom';
import PrivacyPolicy from './pages/PrivacyPolicy';
import TermsOfUse from './pages/TermsOfUse';
import Register from './pages/Register'; 
import ForgotPassword from './pages/ForgotPassword'; 
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { logEvent } from "firebase/analytics";

function Header() {
  return (
    <header className="bg-blue-600 text-white p-4 flex justify-between items-center">
      <nav>
        <ul className="flex space-x-6">
          <li>
            <Link to="/privacypolicy" className="hover:text-gray-300">ÿ≥Ÿäÿßÿ≥ÿ© ÿßŸÑÿÆÿµŸàÿµŸäÿ©</Link>
          </li>
          <li>
            <Link to="/termsofuse" className="hover:text-gray-300">ÿ¥ÿ±Ÿàÿ∑ ÿßŸÑÿßÿ≥ÿ™ÿÆÿØÿßŸÖ</Link>
          </li>
          <li>
            <Link to="/" className="hover:text-gray-300">ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©</Link>
          </li>
        </ul>
      </nav>
      <div className="text-xl font-bold">
        <Link to="/" className="text-white">
          <img src="/secret.png" alt="ÿ¥ÿπÿßÿ± ÿßŸÑŸÖŸàŸÇÿπ" className="h-8" />
        </Link>
      </div>
    </header>
  );
}

function App() {
  const [user, setUser] = useState(null);
  const [nickname, setNickname] = useState('');
  const [message, setMessage] = useState('');
  const [sent, setSent] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [messages, setMessages] = useState([]);
  const [showPassword, setShowPassword] = useState(false); 
  const [password, setPassword] = useState('');
  const [switchOn, setSwitchOn] = useState(false); 
  const [charCount, setCharCount] = useState(0);
  const [loading, setLoading] = useState(true);
  const [isModalOpen, setIsModalOpen] = useState(false); 
  const [selectedMessage, setSelectedMessage] = useState(null); 
  const [email,setEmail] = useState('');
  const [showPopup, setShowPopup] = useState(true); 


  const maxCharCount = 500;
  const auth = getAuth(app);
  const adminEmail = "adham.gh1111@gmail.com"; 


  const handleLogin = async () => {
    try {
      if (!email.trim()) {
        toast.error("ŸÖŸÜ ŸÅÿ∂ŸÑŸÉ ÿ£ÿØÿÆŸÑ ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä");
        return;
      }

      if (!password) {
        toast.error("ŸÖŸÜ ŸÅÿ∂ŸÑŸÉ ÿ£ÿØÿÆŸÑ ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±");
        return;
      }

      await signInWithEmailAndPassword(auth, email.trim(), password);
      toast.success("ÿ™ŸÖ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ÿ®ŸÜÿ¨ÿßÿ≠!");
    } catch (err) {
      console.error("ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ: ", err.code);
      switch (err.code) {
        case 'auth/invalid-email':
          toast.error("ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä ÿ∫Ÿäÿ± ÿµÿßŸÑÿ≠.");
          break;
        case 'auth/user-not-found':
          toast.error("ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä ÿ∫Ÿäÿ± ŸÖŸàÿ¨ŸàÿØ.");
          break;
        case 'auth/wrong-password':
          toast.error("ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ± ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠ÿ©.");
          break;
        default:
          toast.error("ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ∫Ÿäÿ± ŸÖÿπÿ±ŸàŸÅ ÿ£ÿ´ŸÜÿßÿ° ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ.");
      }
    }
  };


  const handleGoogleLogin = async () => {
    const provider = new GoogleAuthProvider();
    try {
      await signInWithPopup(auth, provider);
    } catch (err) {
      alert("ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ÿ®ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿ¨Ÿàÿ¨ŸÑ");
      console.error(err);
    }
  };

  const handleLogout = () => {
    const confirmLogout = window.confirm("ŸáŸÑ ÿ£ŸÜÿ™ ŸÖÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨ÿü");

    if (confirmLogout) {
      signOut(auth)
        .then(() => console.log("ÿ™ŸÖ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨"))
        .catch((error) => console.error("ÿÆÿ∑ÿ£:", error));
    } else {
      console.log("ÿ™ŸÖ ÿ•ŸÑÿ∫ÿßÿ° ÿπŸÖŸÑŸäÿ© ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨");
    }
  };

const sanitizeMessage = (message) => {
  const div = document.createElement('div');
  div.textContent = message;
  return div.innerHTML;
};

const handleSendMessage = async (e) => {
  e.preventDefault(); 

  if (!user) return alert("Ÿäÿ¨ÿ® ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ÿ£ŸàŸÑÿßŸã!");
  if (!nickname.trim() || !message.trim()) return alert("Ÿäÿ±ÿ¨Ÿâ ŸÖŸÑÿ° ÿ¨ŸÖŸäÿπ ÿßŸÑÿ≠ŸÇŸàŸÑ!");

  const safeNickname = sanitizeMessage(nickname);
  const safeMessage = sanitizeMessage(message);

  try {
    const docRef = await addDoc(collection(db, 'messages'), {
      nickname: safeNickname,
      message: safeMessage,
      uid: user.uid,
      createdAt: serverTimestamp()
    });

    console.log("ÿ™ŸÖ ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑÿ±ÿ≥ÿßŸÑÿ© ÿ®ŸÜÿ¨ÿßÿ≠: ", docRef.id); 
    setSent(true);
    setMessage('');
    setNickname('');
  } catch (error) {
    alert("ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑÿ±ÿ≥ÿßŸÑÿ©");
    console.error(error);
  }
};


  const togglePassword = () => {
    setShowPassword(!showPassword);
  };

  const toggleSwitch = () => {
    setSwitchOn(!switchOn);

    if (!switchOn) {
      alert("ÿ¨ŸÖŸäÿπ ŸÖÿπŸÑŸàŸÖÿßÿ™ŸÉ ÿ≥ÿ±Ÿäÿ© ÿßŸÑÿßŸÜ");
    } else {
      alert("ÿ≥Ÿäÿ™ŸÖ ÿßŸÑÿßŸÜ ÿ•ÿ∏Ÿáÿßÿ± ŸÖÿπŸÑŸàŸÖÿßÿ™ŸÉ ÿßŸÑÿ¥ÿÆÿµŸäÿ© ");
    }
  };

  const handleMessageChange = (e) => {
    const newMessage = e.target.value;
    if (newMessage.length <= maxCharCount) {
      setMessage(newMessage);
      setCharCount(newMessage.length); 
    }
  };
  const handleDeleteMessage = async (id) => {
    const confirmDelete = window.confirm("ŸáŸÑ ÿ™ÿ±ŸäÿØ ÿ≠ÿ∞ŸÅ Ÿáÿ∞Ÿá ÿßŸÑÿ±ÿ≥ÿßŸÑÿ©ÿü");

    if (confirmDelete) {
      try {
        await deleteDoc(doc(db, "messages", id));
        setMessages(messages.filter((msg) => msg.id !== id)); 
        alert("ÿ™ŸÖ ÿ≠ÿ∞ŸÅ ÿßŸÑÿ±ÿ≥ÿßŸÑÿ© ÿ®ŸÜÿ¨ÿßÿ≠");
      } catch (error) {
        console.error("ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿßŸÑÿ≠ÿ∞ŸÅ:", error);
        alert("ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ≠ÿ∞ŸÅ ÿßŸÑÿ±ÿ≥ÿßŸÑÿ©");
      }
    }
  };
  const handleShowDetails = (message) => {
    setSelectedMessage(message);
    setIsModalOpen(true); 
  };

  const handleCloseModal = () => {
    setIsModalOpen(false); 
  };



  useEffect(() => {
    logEvent(analytics, 'page_view');

    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
      setIsAdmin(currentUser?.email === adminEmail);
      setLoading(false);

      if (!currentUser) {
        setShowPopup(true);   
      } else {
        setShowPopup(false);

        logEvent(analytics, 'user_login', {
          user_email: currentUser?.email || "unknown",
        });

      }
    });

    return () => unsubscribe();
  }, [auth]);

  useEffect(() => {
    const fetchMessages = async () => {
      if (isAdmin) {
        const q = query(collection(db, 'messages'), orderBy('createdAt', 'desc'));
        const querySnapshot = await getDocs(q);
        const fetchedMessages = querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
        console.log(fetchedMessages);
        setMessages(fetchedMessages);
        setShowPopup(true);
      }
    };

    fetchMessages();
  }, [isAdmin, auth]);
  if (loading) {
    return <div> </div>; 
  }


  const closePopup = () => {
    setShowPopup(false);  
  };


  return (
    <>
      <Header />
      <div>
      {showPopup && (
        <div className="popup">
          <div className="popup-content">
          <h2> Mahmoud ÿ£ÿ±ÿ≥ŸÑ ÿ±ÿ≥ÿßŸÑÿ© ŸÖÿ¨ŸáŸàŸÑÿ© ÿ•ŸÑŸä</h2>
            <p>ŸÜÿ≠ŸÜ ÿ≥ÿπÿØÿßÿ° ÿ®ÿ≤Ÿäÿßÿ±ÿ™ŸÉ ! ÿ•ÿ∞ÿß ŸÉŸÜÿ™ ÿ®ÿ≠ÿßÿ¨ÿ© ÿ•ŸÑŸâ ŸÖÿ≥ÿßÿπÿØÿ©ÿå ŸÑÿß ÿ™ÿ™ÿ±ÿØÿØ ŸÅŸä ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ŸÜÿß.</p>
            <button className="close-btn" onClick={closePopup}>ÿ•ÿ∫ŸÑÿßŸÇ</button>
          </div>
        </div>
      )}

      <Routes>
        <Route path="/privacypolicy" element={<PrivacyPolicy />} />
        <Route path="/termsofuse" element={<TermsOfUse />} />
        <Route path="/register" element={<Register />} />
        <Route path="/forgotpassword" element={<ForgotPassword />} /> 
        <Route path="/" element={loading ? <div>ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ...</div> : (
          <div className="min-h-screen flex items-center justify-center bg-gray-100 p-4">
            <div className="bg-white rounded-2xl shadow-xl p-6 w-full max-w-md text-center">
              {user && isAdmin && (
                <div className="text-right mb-4">
                  <button
                    onClick={handleLogout}
                    className="bg-red-500 text-white px-6 py-2 rounded-lg hover:bg-red-600 transition"
                  >
                    ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨
                  </button>
                </div>
              )}

              {!user ? (
                <div className="space-y-8">
                  <div className="border p-4 rounded-xl shadow-sm">
                    <h2 className="text-lg font-bold mb-4">ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ</h2>

                    <input
                       type="email"
                       className="input-email"
                       value={email}
                       onChange={(e) => setEmail(e.target.value)}
                       placeholder="ÿ£ÿØÿÆŸÑ ÿ®ÿ±ŸäÿØŸÉ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä"
                       />

                    <input
                      type={showPassword ? "text" : "password"}
                      placeholder="ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±"
                      value={password}
                      onChange={(e) => setPassword(e.target.value)}
                      className="input-field"
                    />
                    <div className="checkbox-container">
                      <label htmlFor="showPassword" className="checkbox-label">ÿπÿ±ÿ∂ ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±</label>
                      <input
                        type="checkbox"
                        id="showPassword"
                        checked={showPassword}
                        onChange={togglePassword}
                      />
                    </div>
                    <button 
                            onClick={handleLogin} disabled={loading}
                            className="bg-blue-500 text-white px-4 py-2 rounded-xl hover:bg-blue-600 w-full">
                             ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ 
                    </button>
                    <div className="register-prompt">
                       <p><span className="text-black">ŸÑŸäÿ≥ ŸÑÿØŸäŸÉ ÿ≠ÿ≥ÿßÿ®ÿü </span>
                          <Link to="/register" className="register-link text-blue-600 hover:text-blue-800">
                           ÿ≥ÿ¨ŸÑ ÿ≠ÿ≥ÿßÿ®ŸÉ ÿßŸÑÿ¢ŸÜ
                          </Link>
                       </p>
                      </div>
                      <p>
  <Link to="/forgotpassword">ŸáŸÑ ŸÜÿ≥Ÿäÿ™ ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±ÿü</Link>
</p>
                  </div>
                  <div className="border p-4 rounded-xl shadow-sm">
                    <h2 className="text-lg font-bold mb-4">ÿ™ÿ≥ÿ¨ŸäŸÑ ÿØÿÆŸàŸÑ ŸÖÿ®ÿßÿ¥ÿ±</h2>
                    <button
                      onClick={handleGoogleLogin}
                      className="bg-red-500 text-white px-4 py-2 rounded-xl hover:bg-red-600 w-full flex items-center justify-center space-x-2"
                    >
                      <i className="fab fa-google"></i>
                      <span>ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ÿ®ÿ≠ÿ≥ÿßÿ® ÿ¨Ÿàÿ¨ŸÑ</span>
                    </button>
                  </div>
                </div>
              ) : isAdmin ? (
                <div>
                  <h2 className="text-lg font-semibold mb-2">üì® ÿ¨ŸÖŸäÿπ ÿßŸÑÿ±ÿ≥ÿßÿ¶ŸÑ</h2>
                  <div className="messages-container">
                    <h2>ÿ¨ŸÖŸäÿπ ÿßŸÑÿ±ÿ≥ÿßÿ¶ŸÑ</h2>
                    <table className="messages-table">
                      <thead>
                        <tr>
                          <th>ÿßŸÑÿßÿ≥ŸÖ</th>
                          <th>ÿßŸÑÿ±ÿ≥ÿßŸÑÿ©</th>
                          <th>ÿßŸÑÿ™ÿßÿ±ŸäÿÆ</th>
                          <th>ÿßŸÑÿ™ŸÅÿßÿµŸäŸÑ</th>
                          <th>ÿ≠ÿ∞ŸÅ</th>
                        </tr>
                      </thead>
                      <tbody>
                        {messages.map((msg) => (
                          <tr key={msg.id}>
                            <td>{msg.nickname}</td>
                            <td>{msg.message.substring(0, 50)}...</td>
                            <td>{new Date(msg.createdAt.seconds * 1000).toLocaleString()}</td>
                            <td>
                              <button onClick={() => handleShowDetails(msg)} className="details-btn">
                                ÿ™ŸÅÿßÿµŸäŸÑ
                              </button>
                            </td>
                            <td>
                              <button onClick={() => handleDeleteMessage(msg.id)} className="delete-btn">
                                ÿ≠ÿ∞ŸÅ
                              </button>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>

                    {isModalOpen && (
                      <div className="modal-overlay">
                        <div className="modal-content">
                          <h3>ÿ™ŸÅÿßÿµŸäŸÑ ÿßŸÑÿ±ÿ≥ÿßŸÑÿ©</h3>
                          <p><strong>ÿßŸÑÿßÿ≥ŸÖ ÿßŸÑŸÖÿ≥ÿ™ÿπÿßÿ±:</strong> {selectedMessage.nickname}</p>
                          <p><strong>ÿßŸÑÿ±ÿ≥ÿßŸÑÿ© ŸÉÿßŸÖŸÑÿ©:</strong> {selectedMessage.message}</p>
                          <button onClick={handleCloseModal} className="close-btn">ÿ•ÿ∫ŸÑÿßŸÇ</button>
                        </div>
                      </div>
                    )}
                  </div>
                </div>
              ) : (
                <>
                  <form onSubmit={handleSendMessage} className="form-container">
                    <h2 className="text-xl font-semibold mb-4">  Mahmoud ÿ•ÿ±ÿ≥ÿßŸÑ ÿ±ÿ≥ÿßŸÑÿ© ŸÖÿ¨ŸáŸàŸÑÿ© .ÿßŸÑŸä </h2>
                    <input
                      type="text"
                      placeholder="ÿßÿ≥ŸÖŸÉ ÿßŸÑŸÖÿ≥ÿ™ÿπÿßÿ±"
                      value={nickname}
                      onChange={(e) => setNickname(e.target.value)}
                      className="input-field"
                    />
                    <textarea
                      placeholder="ÿßŸÉÿ™ÿ® ÿ±ÿ≥ÿßŸÑÿ™ŸÉ ŸáŸÜÿß ÿ®ÿØŸàŸÜ ÿßŸÜ Ÿäÿπÿ±ŸÅŸÉ..."
                      value={message}
                      onChange={handleMessageChange}
                      className="textarea-field"
                    />
                    <div className="flex justify-between items-center mt-2">
                      <span>{charCount}/{maxCharCount}</span>
                      {charCount === maxCharCount && <span className="text-red-500">ŸÑŸÇÿØ ŸàÿµŸÑÿ™ ÿ•ŸÑŸâ ÿßŸÑÿ≠ÿØ ÿßŸÑÿ£ŸÇÿµŸâ!</span>}
                    </div>
                    <button
                      type="submit"
                      disabled={charCount > maxCharCount}
                      className="submit-button mt-4"
                    >
                      ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑÿ±ÿ≥ÿßŸÑÿ©
                    </button>
                    {sent && <p className="text-green-500 mt-3">ÿ™ŸÖ ÿ•ÿ±ÿ≥ÿßŸÑ ÿ±ÿ≥ÿßŸÑÿ™ŸÉ ÿ®ŸÜÿ¨ÿßÿ≠ ‚úÖ</p>}
                    <div className="flex justify-end items-center mt-6">
                      <span className="ml-3 text-lg font-semibold">{switchOn ? "ÿ®ÿ¥ŸÉŸÑ ÿ≥ÿ±Ÿä" : "ÿ®ÿ¥ŸÉŸÑ ÿ∫Ÿäÿ± ÿ≥ÿ±Ÿä"}</span>
                      <label className="switch small">
                        <input type="checkbox" checked={switchOn} onChange={toggleSwitch} />
                        <span className="slider"></span>
                      </label>
                    </div>
                  </form>
                  <div className="mt-6">
                    <form onSubmit={handleLogout} className="text-center">
                      <button
                        type="submit"
                        className="w-full px-4 py-2 rounded-xl text-white bg-red-600 hover:bg-red-700"
                      >
                        ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨
                      </button>
                    </form>
                  </div>
                </>
              )}
            </div>
          </div>
        )}
        />
      </Routes>
      <ToastContainer/>
      </div>

    </>
  );
}

export default App;
