import React, { useState, useEffect } from 'react';
import { getAuth, signInWithPopup, GoogleAuthProvider, onAuthStateChanged } from 'firebase/auth';
import { app } from './firebase'; // ุชุฃูุฏ ุฃู firebase.js ููุฌูุฏ ูููุนุฏ ููู Firebase
import { collection, addDoc, serverTimestamp, getDocs, orderBy, query } from 'firebase/firestore';
import { db } from './firebase'; // ุฃู firebaseConfig ุญุณุจ ุงุณู ุงูููู
import './App.css';
import { signOut } from "firebase/auth";
import { deleteDoc, doc } from "firebase/firestore"; // ูู ูุง ุงุณุชูุฑุฏุชู ูููุ ุถููู ููู
import { Link } from 'react-router-dom';

function Header() {
  return (
    <header className="bg-blue-600 text-white p-4 flex justify-between items-center">
      <div className="text-xl font-bold">
        <Link to="/" className="text-white">ุดุนุงุฑู</Link>
      </div>
      <nav>
        <ul className="flex space-x-6">
          <li>
            <Link to="/" className="hover:text-gray-300">ุงูุฑุฆูุณูุฉ</Link>
          </li>
          <li>
            <Link to="/register" className="hover:text-gray-300">ุงูุชุณุฌูู</Link>
          </li>
          <li>
            <Link to="/login" className="hover:text-gray-300">ุชุณุฌูู ุงูุฏุฎูู</Link>
          </li>
        </ul>
      </nav>
    </header>
  );
}

function App() {
  const [user, setUser] = useState(null);
  const [nickname, setNickname] = useState('');
  const [message, setMessage] = useState('');
  const [sent, setSent] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [messages, setMessages] = useState([]);
  const [showPassword, setShowPassword] = useState(false); // ุญุงูุฉ ุฅุธูุงุฑ ูููุฉ ุงููุฑูุฑ
  const [password, setPassword] = useState('');
  const [switchOn, setSwitchOn] = useState(false); // ุญุงูุฉ ุงูุณููุชุด
  const [charCount, setCharCount] = useState(0);
  const [loading, setLoading] = useState(true);
  const [isModalOpen, setIsModalOpen] = useState(false); // ูุชุญุฏูุฏ ุฅุฐุง ูุงูุช ุงููุงูุฐุฉ ุงูููุจุซูุฉ ููุชูุญุฉ ุฃู ูุง
  const [selectedMessage, setSelectedMessage] = useState(null); // ูุญูุธ ุงูุฑุณุงูุฉ ุงููุฎุชุงุฑุฉ
    const maxCharCount = 500;




  const auth = getAuth(app);
  const provider = new GoogleAuthProvider();
  const adminEmail = "adham.gh1111@gmail.com"; // โ ุนุฏูู ุงูุฅูููู ุญุณุจ ูุง ุชุฑูุฏ

  const handleLogin = async () => {
    try {
      await signInWithPopup(auth, provider);
    } catch (err) {
      alert("ุญุฏุซ ุฎุทุฃ ุฃุซูุงุก ุชุณุฌูู ุงูุฏุฎูู");
      console.error(err);
    }
  };

  const handleLogout = () => {
    const confirmLogout = window.confirm("ูู ุฃูุช ูุชุฃูุฏ ูู ุชุณุฌูู ุงูุฎุฑูุฌุ");

    if (confirmLogout) {
      signOut(auth)
        .then(() => console.log("ุชู ุชุณุฌูู ุงูุฎุฑูุฌ"))
        .catch((error) => console.error("ุฎุทุฃ:", error));
    } else {
      console.log("ุชู ุฅูุบุงุก ุนูููุฉ ุชุณุฌูู ุงูุฎุฑูุฌ");
    }
  };


  const handleSendMessage = async (e) => {
    e.preventDefault(); // ๐ ูุฐุง ูููุน ุฅุนุงุฏุฉ ุชุญููู ุงูุตูุญุฉ

    if (!user) return alert("ูุฌุจ ุชุณุฌูู ุงูุฏุฎูู ุฃููุงู!");
    if (!nickname.trim() || !message.trim()) return alert("ูุฑุฌู ููุก ุฌููุน ุงูุญููู!");

    try {
      // ุฅุฑุณุงู ุงูุฑุณุงูุฉ ุฅูู Firebase
      const docRef = await addDoc(collection(db, 'messages'), {
        nickname,
        message,
        uid: user.uid,
        createdAt: serverTimestamp()
      });

      console.log("ุชู ุฅุฑุณุงู ุงูุฑุณุงูุฉ ุจูุฌุงุญ: ", docRef.id); // ุฅุถุงูุฉ log ููุง
      setSent(true);
      setMessage('');
      setNickname('');
    } catch (error) {
      alert("ุญุฏุซ ุฎุทุฃ ุฃุซูุงุก ุฅุฑุณุงู ุงูุฑุณุงูุฉ");
      console.error(error);
    }
  };


  const togglePassword = () => {
    setShowPassword(!showPassword);
  };

  const toggleSwitch = () => {
    setSwitchOn(!switchOn);

    if (!switchOn) {
      alert("ุฌููุน ูุนูููุงุชู ุณุฑูุฉ ุงูุงู");
    } else {
      alert("ุณูุชู ุงูุงู ุฅุธูุงุฑ ูุนูููุงุชู ุงูุดุฎุตูุฉ ");
    }
  };

  const handleMessageChange = (e) => {
    const newMessage = e.target.value;
    if (newMessage.length <= maxCharCount) {
      setMessage(newMessage);
      setCharCount(newMessage.length); // ุชุญุฏูุซ ุนุฏุฏ ุงูุญุฑูู
    }
  };
  const handleDeleteMessage = async (id) => {
    const confirmDelete = window.confirm("ูู ุชุฑูุฏ ุญุฐู ูุฐู ุงูุฑุณุงูุฉุ");

    if (confirmDelete) {
      try {
        await deleteDoc(doc(db, "messages", id));
        setMessages(messages.filter((msg) => msg.id !== id)); // ูุญุฐููุง ููุงู ูู ุงููุงุฌูุฉ
        alert("ุชู ุญุฐู ุงูุฑุณุงูุฉ ุจูุฌุงุญ");
      } catch (error) {
        console.error("ุฎุทุฃ ุฃุซูุงุก ุงูุญุฐู:", error);
        alert("ุญุฏุซ ุฎุทุฃ ุฃุซูุงุก ุญุฐู ุงูุฑุณุงูุฉ");
      }
    }
  };
  const handleShowDetails = (message) => {
    setSelectedMessage(message);
    setIsModalOpen(true); // ูุชุญ ุงููุงูุฐุฉ ุงูููุจุซูุฉ
  };

  const handleCloseModal = () => {
    setIsModalOpen(false); // ุฅุบูุงู ุงููุงูุฐุฉ ุงูููุจุซูุฉ
  };



  useEffect(() => {
    onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
      setIsAdmin(currentUser?.email === adminEmail);
      setLoading(false);  // ุนูุฏ ุงูุชูุงู ุงูุชุญูู ูู ุญุงูุฉ ุงููุณุชุฎุฏู
    });
  }, [auth]);

  useEffect(() => {
    const fetchMessages = async () => {
      if (isAdmin) {
        const q = query(collection(db, 'messages'), orderBy('createdAt', 'desc'));
        const querySnapshot = await getDocs(q);
        const fetchedMessages = querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
        console.log(fetchedMessages); // ุฅุถุงูุฉ log ููุง ููุชุญูู
        setMessages(fetchedMessages);
      }
    };

    fetchMessages();
  }, [isAdmin, auth]);
  if (loading) {
    return <div> </div>; 
  }


  return ( 


    <div className="min-h-screen flex items-center justify-center bg-gray-100 p-4">
      <div className="bg-white rounded-2xl shadow-xl p-6 w-full max-w-md text-center">

        {/* ุฒุฑ ุชุณุฌูู ุงูุฎุฑูุฌ ุฅุฐุง ูุงู ุงููุดุฑู */}
        {user && isAdmin && (
          <div className="text-right mb-4">
            <button
              onClick={handleLogout}
              className="bg-red-500 text-white px-6 py-2 rounded-lg hover:bg-red-600 transition"
            >
              ุชุณุฌูู ุงูุฎุฑูุฌ
            </button>
          </div>
        )}

        {/* ุฅุฐุง ูู ูุณุฌู ุงููุณุชุฎุฏู ุงูุฏุฎูู */}
