import React, { useState, useEffect } from 'react';
import { getAuth, signInWithPopup, GoogleAuthProvider, onAuthStateChanged } from 'firebase/auth';
import { app } from './firebase'; // ÿ™ÿ£ŸÉÿØ ÿ£ŸÜ firebase.js ŸÖŸàÿ¨ŸàÿØ ŸàŸÖŸèÿπÿØ ŸÅŸäŸá Firebase
import { collection, addDoc, serverTimestamp, getDocs, orderBy, query } from 'firebase/firestore';
import { db } from './firebase'; // ÿ£Ÿà firebaseConfig ÿ≠ÿ≥ÿ® ÿßÿ≥ŸÖ ÿßŸÑŸÖŸÑŸÅ
import './App.css';
import { signOut } from "firebase/auth";
import { deleteDoc, doc } from "firebase/firestore"; // ŸÑŸà ŸÖÿß ÿßÿ≥ÿ™Ÿàÿ±ÿØÿ™Ÿá ŸÅŸàŸÇÿå ÿ∂ŸäŸÅŸá ŸÅŸàŸÇ
import { Link } from 'react-router-dom';
import { Routes, Route } from 'react-router-dom'; // ÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿßÿ≥ÿ™Ÿäÿ±ÿßÿØ Routes Ÿà Route ŸÅŸÇÿ∑
import PrivacyPolicy from './pages/PrivacyPolicy';
import TermsOfUse from './pages/TermsOfUse';

function Header() {
  return (
    <header className="bg-blue-600 text-white p-4 flex justify-between items-center">
      <nav>
        <ul className="flex space-x-6">
          <li>
            <Link to="/PrivacyPolicy" className="hover:text-gray-300">ÿ≥Ÿäÿßÿ≥ÿ© ÿßŸÑÿÆÿµŸàÿµŸäÿ©</Link>
          </li>
          <li>
            <Link to="/TermsOfUse" className="hover:text-gray-300">ÿ¥ÿ±Ÿàÿ∑ ÿßŸÑÿ•ÿ≥ÿ™ÿÆÿØÿßŸÖ</Link>
          </li>
          <li>
            <Link to="/" className="hover:text-gray-300">ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©</Link>
          </li>
        </ul>
      </nav>
      <div className="text-xl font-bold">
        <Link to="/" className="text-white">
          <img src="/secret.png" alt="ÿ¥ÿπÿßÿ± ÿßŸÑŸÖŸàŸÇÿπ" className="h-8" />
        </Link>
      </div>
    </header>
  );
}

function App() {
  const [user, setUser] = useState(null);
  const [nickname, setNickname] = useState('');
  const [message, setMessage] = useState('');
  const [sent, setSent] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [messages, setMessages] = useState([]);
  const [showPassword, setShowPassword] = useState(false); // ÿ≠ÿßŸÑÿ© ÿ•ÿ∏Ÿáÿßÿ± ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±
  const [password, setPassword] = useState('');
  const [switchOn, setSwitchOn] = useState(false); // ÿ≠ÿßŸÑÿ© ÿßŸÑÿ≥ŸàŸäÿ™ÿ¥
  const [charCount, setCharCount] = useState(0);
  const [loading, setLoading] = useState(true);
  const [isModalOpen, setIsModalOpen] = useState(false); // ŸÑÿ™ÿ≠ÿØŸäÿØ ÿ•ÿ∞ÿß ŸÉÿßŸÜÿ™ ÿßŸÑŸÜÿßŸÅÿ∞ÿ© ÿßŸÑŸÖŸÜÿ®ÿ´ŸÇÿ© ŸÖŸÅÿ™Ÿàÿ≠ÿ© ÿ£ŸÖ ŸÑÿß
  const [selectedMessage, setSelectedMessage] = useState(null); // ŸÑÿ≠ŸÅÿ∏ ÿßŸÑÿ±ÿ≥ÿßŸÑÿ© ÿßŸÑŸÖÿÆÿ™ÿßÿ±ÿ©
    const maxCharCount = 500;


  const auth = getAuth(app);
  const provider = new GoogleAuthProvider();
  const adminEmail = "adham.gh1111@gmail.com"; 

  const handleLogin = async () => {
    try {
      await signInWithPopup(auth, provider);
    } catch (err) {
      alert("ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ");
      console.error(err);
    }
  };

  const handleLogout = () => {
    const confirmLogout = window.confirm("ŸáŸÑ ÿ£ŸÜÿ™ ŸÖÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨ÿü");

    if (confirmLogout) {
      signOut(auth)
        .then(() => console.log("ÿ™ŸÖ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨"))
        .catch((error) => console.error("ÿÆÿ∑ÿ£:", error));
    } else {
      console.log("ÿ™ŸÖ ÿ•ŸÑÿ∫ÿßÿ° ÿπŸÖŸÑŸäÿ© ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨");
    }
  };


  const handleSendMessage = async (e) => {
    e.preventDefault(); // üëà Ÿáÿ∞ÿß ŸäŸÖŸÜÿπ ÿ•ÿπÿßÿØÿ© ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿµŸÅÿ≠ÿ©

    if (!user) return alert("Ÿäÿ¨ÿ® ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ÿ£ŸàŸÑÿßŸã!");
    if (!nickname.trim() || !message.trim()) return alert("Ÿäÿ±ÿ¨Ÿâ ŸÖŸÑÿ° ÿ¨ŸÖŸäÿπ ÿßŸÑÿ≠ŸÇŸàŸÑ!");

    try {
      // ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑÿ±ÿ≥ÿßŸÑÿ© ÿ•ŸÑŸâ Firebase
      const docRef = await addDoc(collection(db, 'messages'), {
        nickname,
        message,
        uid: user.uid,
        createdAt: serverTimestamp()
      });

      console.log("ÿ™ŸÖ ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑÿ±ÿ≥ÿßŸÑÿ© ÿ®ŸÜÿ¨ÿßÿ≠: ", docRef.id); // ÿ•ÿ∂ÿßŸÅÿ© log ŸáŸÜÿß
      setSent(true);
      setMessage('');
      setNickname('');
    } catch (error) {
      alert("ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑÿ±ÿ≥ÿßŸÑÿ©");
      console.error(error);
    }
  };


  const togglePassword = () => {
    setShowPassword(!showPassword);
  };

  const toggleSwitch = () => {
    setSwitchOn(!switchOn);

    if (!switchOn) {
      alert("ÿ¨ŸÖŸäÿπ ŸÖÿπŸÑŸàŸÖÿßÿ™ŸÉ ÿ≥ÿ±Ÿäÿ© ÿßŸÑÿßŸÜ");
    } else {
      alert("ÿ≥Ÿäÿ™ŸÖ ÿßŸÑÿßŸÜ ÿ•ÿ∏Ÿáÿßÿ± ŸÖÿπŸÑŸàŸÖÿßÿ™ŸÉ ÿßŸÑÿ¥ÿÆÿµŸäÿ© ");
    }
  };

  const handleMessageChange = (e) => {
    const newMessage = e.target.value;
    if (newMessage.length <= maxCharCount) {
      setMessage(newMessage);
      setCharCount(newMessage.length); // ÿ™ÿ≠ÿØŸäÿ´ ÿπÿØÿØ ÿßŸÑÿ≠ÿ±ŸàŸÅ
    }
  };
  const handleDeleteMessage = async (id) => {
    const confirmDelete = window.confirm("ŸáŸÑ ÿ™ÿ±ŸäÿØ ÿ≠ÿ∞ŸÅ Ÿáÿ∞Ÿá ÿßŸÑÿ±ÿ≥ÿßŸÑÿ©ÿü");

    if (confirmDelete) {
      try {
        await deleteDoc(doc(db, "messages", id));
        setMessages(messages.filter((msg) => msg.id !== id)); // ŸÜÿ≠ÿ∞ŸÅŸáÿß ŸÉŸÖÿßŸÜ ŸÖŸÜ ÿßŸÑŸàÿßÿ¨Ÿáÿ©
        alert("ÿ™ŸÖ ÿ≠ÿ∞ŸÅ ÿßŸÑÿ±ÿ≥ÿßŸÑÿ© ÿ®ŸÜÿ¨ÿßÿ≠");
      } catch (error) {
        console.error("ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿßŸÑÿ≠ÿ∞ŸÅ:", error);
        alert("ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ≠ÿ∞ŸÅ ÿßŸÑÿ±ÿ≥ÿßŸÑÿ©");
      }
    }
  };
  const handleShowDetails = (message) => {
    setSelectedMessage(message);
    setIsModalOpen(true); // ŸÅÿ™ÿ≠ ÿßŸÑŸÜÿßŸÅÿ∞ÿ© ÿßŸÑŸÖŸÜÿ®ÿ´ŸÇÿ©
  };

  const handleCloseModal = () => {
    setIsModalOpen(false); // ÿ•ÿ∫ŸÑÿßŸÇ ÿßŸÑŸÜÿßŸÅÿ∞ÿ© ÿßŸÑŸÖŸÜÿ®ÿ´ŸÇÿ©
  };



  useEffect(() => {
    onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
      setIsAdmin(currentUser?.email === adminEmail);
      setLoading(false);  // ÿπŸÜÿØ ÿßŸÉÿ™ŸÖÿßŸÑ ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿ≠ÿßŸÑÿ© ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ
    });
  }, [auth]);

  useEffect(() => {
    const fetchMessages = async () => {
      if (isAdmin) {
        const q = query(collection(db, 'messages'), orderBy('createdAt', 'desc'));
        const querySnapshot = await getDocs(q);
        const fetchedMessages = querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
        console.log(fetchedMessages); // ÿ•ÿ∂ÿßŸÅÿ© log ŸáŸÜÿß ŸÑŸÑÿ™ÿ≠ŸÇŸÇ
        setMessages(fetchedMessages);
      }
    };

    fetchMessages();
  }, [isAdmin, auth]);
  if (loading) {
    return <div> </div>; 
  }


  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100 p-4">
      <div className="bg-white rounded-2xl shadow-xl p-6 w-full max-w-md text-center">

        {user && isAdmin && (
          <div className="text-right mb-4">
            <button
              onClick={handleLogout}
              className="bg-red-500 text-white px-6 py-2 rounded-lg hover:bg-red-600 transition"
            >
              ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨
            </button>
          </div>
        )}

        {!user ? (
          <div className="space-y-8">
            <div className="border p-4 rounded-xl shadow-sm">
              <h2 className="text-lg font-bold mb-4">ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ</h2>

              <input
                type="text"
                placeholder="ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä"
                className="border p-2 w-full rounded mb-3"
              />

              <input
                type={showPassword ? "text" : "password"}
                placeholder="ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="input-field"
              />

              <div className="checkbox-container">
                <label htmlFor="showPassword" className="checkbox-label">ÿπÿ±ÿ∂ ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±</label>
                <input
                  type="checkbox"
                  id="showPassword"
                  checked={showPassword}
                  onChange={togglePassword}
                />
              </div>

              <button
                onClick={() => alert("ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ÿßŸÑÿ™ŸÇŸÑŸäÿØŸä ŸÇŸäÿØ ÿßŸÑÿ™ÿ∑ŸàŸäÿ± üòÖ")}
                className="bg-blue-500 text-white px-4 py-2 rounded-xl hover:bg-blue-600 w-full"
              >
                ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ
              </button>

              <div className="register-prompt">
                <p><span className="text-black">ŸÑŸäÿ≥ ŸÑÿØŸäŸÉ ÿ≠ÿ≥ÿßÿ®ÿü </span><a href="#" className="register-link">ÿ≥ÿ¨ŸÑ ÿ≠ÿ≥ÿßÿ®ŸÉ ÿßŸÑÿ¢ŸÜ</a></p>
              </div>
            </div>

            <div className="border p-4 rounded-xl shadow-sm">
              <h2 className="text-lg font-bold mb-4">ÿ™ÿ≥ÿ¨ŸäŸÑ ÿØÿÆŸàŸÑ ŸÖÿ®ÿßÿ¥ÿ±</h2>
              <button
                onClick={handleLogin}
                className="bg-red-500 text-white px-4 py-2 rounded-xl hover:bg-red-600 w-full flex items-center justify-center space-x-2"
