import React, { useState } from 'react';
import { createUserWithEmailAndPassword, getAuth, updateProfile } from 'firebase/auth';
import { useNavigate } from 'react-router-dom'; // استيراد useNavigate
import { app } from '../firebase'; // تأكد من استيراد firebase
import ReCAPTCHA from 'react-google-recaptcha'; // استيراد مكتبة reCAPTCHA
import './Register.css';  // استيراد ملف الـ CSS

const auth = getAuth(app);

function Register() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState('');
  const [error, setError] = useState('');
  const [captchaValue, setCaptchaValue] = useState(null); // قيمة captcha
  const [isLoading, setIsLoading] = useState(false); // حالة لعرض التحميل المؤقت
  const navigate = useNavigate(); // تهيئة navigate

  const handleRegister = async (e) => {
    e.preventDefault();

    // التحقق من الحقول المطلوبة
    if (!email || !password || !name) {
      setError('جميع الحقول مطلوبة');
      return;
    }

    // التحقق من طول كلمة المرور
    if (password.length < 6) {
      setError('كلمة المرور يجب أن تكون على الأقل 6 أحرف');
      return;
    }

    // التحقق من أن المستخدم قد أكمل reCAPTCHA
    if (!captchaValue) {
      setError('يجب التحقق من أنك لست روبوتًا');
      return;
    }

    try {
      setIsLoading(true); // تفعيل حالة التحميل عند بداية التسجيل

      // إنشاء المستخدم
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;

      // تحديث اسم المستخدم
      await updateProfile(user, { displayName: name });

      // تأخير التوجيه إلى الصفحة الرئيسية لمدة 2 ثانية
      setTimeout(() => {
        setIsLoading(false); // إيقاف حالة التحميل
        navigate("/"); // التوجيه إلى الصفحة الرئيسية بعد التأخير
      }, 8000);
      
    } catch (error) {
      setIsLoading(false); // إيقاف حالة التحميل في حالة وجود خطأ
      setError('حدث خطأ أثناء إنشاء الحساب: ' + error.message);
      console.error(error);
    }
  };

  // التعامل مع تحقق captcha
  const onCaptchaChange = (value) => {
    setCaptchaValue(value);
  };

  return (
    <div className="register-container">
      <h2>تسجيل حساب جديد</h2>
      {error && <p className="error">{error}</p>}
      {isLoading && <p className="loading">جاري إنشاء الحساب... يرجى الانتظار.</p>} {/* عرض رسالة التحميل */}
      <form onSubmit={handleRegister}>
        <input
          type="text"
          placeholder="الاسم"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <input
          type="email"
          placeholder="البريد الإلكتروني"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="password"
          placeholder="كلمة المرور"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        
        {/* إضافة reCAPTCHA */}
        <ReCAPTCHA
          sitekey="6LeD7iUrAAAAAOs2-cdQfQ1l_PUmXjOMOIAEiaWv" // استبدل هذا بمفتاح الـ Site الخاص بك من Google
          onChange={onCaptchaChange}
        />
        
        <button type="submit">تسجيل</button>
      </form>
    </div>
  );
}

export default Register;
